import React, { FormEvent } from 'react';
import { Company } from './Entitis.type';
import { useState, useCallback } from 'react';

type Props = {
  token: string;
};

function UpdateCompany(props: Props): JSX.Element {
  const [company, setCompany] = useState<Company>({ id:0, name: '', email: '',password:0,coupons:[] });

  const updateCompany =(event: FormEvent<HTMLFormElement>): void => {
      event.preventDefault(); // prevent page refresh
      console.log(props.token);

      fetch('http://localhost:8080/admin/admin/company/update', {
        method: 'POST',
        headers: {
          Authorization: props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
        body: JSON.stringify({
          id:company.id,
          name: company.name,
          email: company.email,
          password:company.password
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          setCompany(data);
          console.log(data); // log response data to console
        })
        .catch((error) => console.log(error));
      }

  return (
    <div className="App">
      <form onSubmit={updateCompany}>
        <label>ID: </label>
        <input type="u" value={company.id} onChange={(event) => setCompany((prev: any) => ({ ...prev, id: event.target.value }))} /><br/>
        <label>Name: </label>
        <input type="text" value={company.name} onChange={(event) => setCompany((prev: any) => ({ ...prev, name: event.target.value }))} /><br/>
        <label>Email: </label>
        <input type="email" value={company.email} onChange={(event) => setCompany((prev: any) => ({ ...prev, email: event.target.value }))} /><br/>
        <label>Password: </label>
        <input type="password" value={company.password} onChange={(event) => setCompany((prev: any) => ({ ...prev, password: event.target.value }))} /><br/>
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default UpdateCompany;
