import React, { useState } from 'react'
import { Coupon } from '../../Entitis.type';
interface Props {
  token: string;
  id:number
}
const CompanyUpdateCoupon = (props: Props) => {
  const [coupon, setCoupon] = useState<Coupon>(
    {id:undefined,  category: undefined,
    startDate: '',
    endDate: '',
    amount: undefined,
    price: undefined,
    image: '' });
    const updateCoupon =(event: React.MouseEvent): void => {
      event.preventDefault(); // prevent page refresh
      console.log(props.token);

      fetch('http://localhost:8080/company/addCoupon?companyId='+props.id, { 
        method: 'POST',
        headers: {
          Authorization: props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
        body: JSON.stringify(coupon),
      }).catch((error) => console.log(error));//send to logs instead of prnting to console
      }
  const url='http://localhost:8080/company/updateCoupon'
  return (
    <form>
          <label>Id: </label>
    <input type="category" value={coupon.id} onChange={(event) => setCoupon((prev: any) => ({ ...prev, id: event.target.value }))} /><br/>
    <label>Category: </label>
    <input type="category" value={coupon.category} onChange={(event) => setCoupon((prev: any) => ({ ...prev, category: event.target.value }))} /><br/>
    <label>Price: </label>
    <input type="price" value={coupon.price} onChange={(event) => setCoupon((prev: any) => ({ ...prev, price: event.target.value }))} /><br/>
    <label>Amount: </label>
    <input type="amount" value={coupon.amount} onChange={(event) => setCoupon((prev: any) => ({ ...prev, amount: event.target.value }))} /><br/>
    <label>End Date Coupon: </label>
    <input type="amount" value={coupon.endDate} onChange={(event) => setCoupon((prev: any) => ({ ...prev, endDate: event.target.value }))} /><br/>
    <button type="button" onClick={updateCoupon}>add company</button>
  </form>
  )
}

export default CompanyUpdateCoupon
