import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import '../../Design/GetAllCompanys.css'
import { useNavigate } from 'react-router-dom';
import UpdateCompany from './UpdateCompany';

type Props = {
  token: string
};

const GetAllCompanys = (props: Props) => {
  const navigate = useNavigate();
  const [company, setCompany] = useState<Array<Company>>([]);
  const [showUpdateCompany, setShowUpdateCompany] = useState(false);
  const [companyIdToUpdate, setCompanyIdToUpdate] = useState<string>('');

  useEffect(() => {
    fetch('http://localhost:8080/admin/company/all', {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    })
    .then((res) => res.json())
    .then((data) => {
      setCompany(data)
    })
    .catch((error) => console.log(error));
  }, []);

  const handleUpdateCompanyClick = (companyId: string) => {
    setCompanyIdToUpdate(companyId);
    setShowUpdateCompany(true);
  }

  const handleUpdateCompanyClose = () => {
    setShowUpdateCompany(false);
  }

  return (
    <div className="getAllCompanys">
      <button onClick={() => navigate(-1)}>Back</button>
      <table>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Category</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {company.map((company) => {
            return (
              <tr key={company.id}>
                <td>
                  <button onClick={() => handleUpdateCompanyClick(company.id)}>
                    {company.name}
                  </button>
                </td>
                <td>
                  {company.coupons.map((coupon) => {
                    return (
                      <div key={coupon.id}>
                        {coupon.category && <div>{coupon.category}</div>}
                      </div>
                    );
                  })}
                </td>
                <td>
                  {company.coupons.map((coupon) => {
                    return (
                  <div key={coupons.id}>
                    {coupons.price && <div>{coupons.price}</div>}
                  </div>
                );
              })}
            </td>
          </tr>
        );
      })}
    </tbody>
  </table>
</div>

  )
}

export default GetAllCompanys;
