import { Customer } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import React from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';

type Props = {
  token: string
  
};

export const GetAllCustomers = (props: Props) => {
  
  
  const navigate = useNavigate();

  const [customers, setCustomers] = useState<Array<Customer>>([]);

  useEffect(() => {
    fetch('http://localhost:8080/admin/customers/all', {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    })
      .then((res) => res.json())
      .then((data) => {
        setCustomers(data)
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <div className='getAllCustomers'>
          <button onClick={() => navigate(-1)}>Back</button> {/* add "Back" button */}

        <table>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Category</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => {
            return (
              <tr key={customer.id}>
                
                <td>
                  <Link to={`/getOneCustomer/${customer.id}`}>
                    {customer.firstName}
                  </Link>
                </td>
                <td>
                  <div>{customer.lastName}</div>
                </td> 
                <td>
                  {customer.coupons.map((coupons) => {
                    return (
                      <div key={coupons.id}>
                        {coupons.category && <div>{coupons.category}</div>}
                      </div>
                    );
                  })}
                </td>
                <td>
                  {customer.coupons.map((coupons) => {
                    return (
                      <div key={coupons.id}>
                        {coupons.price && <div>{coupons.price}</div>}
                      </div>
                    );
                  })}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <div className="add">
        <Link to='/addCustomer'>Add Customer</Link>   
      </div>
    </div>
  )
}
