import { useState, FormEvent } from 'react';
type Props = {
    token: string
  };
export const DeleteCustomer = (props: Props) => {

  const [id, setId] = useState<number>(0);

  const deleteCustomer = (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
    fetch('http://localhost:8080/admin/customer/delete?id=' + id, {
      method: 'POST',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).catch((error) => console.log(error));
  }

  return (
    <div className='delete-company'>{
      <form onSubmit={deleteCustomer}>
        <label>id: </label>
        <input type="id" value={id} onChange={(event) => setId(parseInt(event.target.value))}  />
        <button type="submit">delete customer</button>
      </form>}
    </div>
  )
}

export default DeleteCustomer ;
