import React, { useState, } from 'react';
import './App.css';

function App() {
  const [loginToken,setLoginToken]=useState("");
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');

   const login=(event: FormEvent<HTMLFormElement>)=>{
    event.preventDefault(); // prevent page refresh

    fetch("http://localhost:8080/login",{
      headers:{
        "Content-Type":"application/json",      
      },
      body:JSON.stringify({
        "email": email,
        "password": password,
        "userType": userType
      }),
      mode:"cors",
      method:"POST"
    })
    .then((data)=>data.text())
    .then((token)=>setLoginToken(token))
    .catch((error) => console.log(error))
  }
  console.log(loginToken)
  return (
    <div className="App">   
    <form onSubmit={login}>
      <label>Email: </label>
      <input type="email: " value={email} onChange={(event) => setEmail(event.target.value)} /><br/>
      <label>Password: </label>
      <input type="password: " value={password} onChange={(event) => setPassword(event.target.value)} /><br/>
      <label>user type: </label>
      <input type="password: " value={userType} onChange={(event) => setUserType(event.target.value)} /><br/>
      <button type="submit">Login</button>
    </form>
    </div>
  );
}
export default App;
