import { Customer } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import React from 'react';

type Props = {
  token: string
  
};

export const GetAllCustomers = (props: Props) => {
  const [customers, setCustomers] = useState<Array<Customer>>([]);

  useEffect(() => {
    fetch('http://localhost:8080/admin/customers/all', {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    })
      .then((res) => res.json())
      .then((data) => {
        setCustomers(data)
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <div className='getAllCustomers'>
      {customers.map((customer) => {
        return (
          <React.Fragment key={customer.id}>
            <tr className='customer'>
              <td>{customer.firstName}</td>
              <td>{customer.lastName}</td>
            </tr>
            <tr>
              <td>coupons:</td>
              <td>
                {customer.coupons.map((coupon) => {
                  return (
                    <React.Fragment key={coupon.id}>
                      <div>category: {coupon.category}</div>
                      <div>price: {coupon.price}</div>
                    </React.Fragment>
                  )
                })}
              </td>
            </tr>
          </React.Fragment>
        )
      })}
    </div>
  )
}
