import  { FormEvent, useEffect, useState } from 'react'
import { Coupon } from '../../Entitis.type';
interface Props {
    token: string|undefined
    id:number|undefined
  }
const CompanyGetAllCoupons = (props: Props) => {
  const url='http://localhost:8080/company/getCoupons?companyId='+props.id;
  
  const [coupons, setCoupons] = useState<Array<Coupon>>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [hasError, setHasError] = useState<boolean>(false);
  console.log(props.token);

  const GetByMaxPrice = async (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
    setLoading(true);
    setHasError(false);

    try {
      const response = await fetch(
        `http://localhost:8080/company/getCoupons/maxToPay?companyId=${props.id}&maxPrice=${maxPrice}`,
        {
          method: 'GET',
          headers: {
            Authorization: props.token!,
            'Content-Type': 'application/json',
          },
          mode: 'cors',
        }
      );

      if (response.ok) {
        const data = await response.json();
        setCoupons(data);
        console.log(data); // log response data to console
      } else {
        console.log('Response not ok');
        setHasError(true);
      }
    } catch (error) {
      console.log(error);
      setHasError(true);
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className='getAllCompanys'>
      <form onSubmit={GetByMaxPrice}>
        <label>Max Price:</label>
        //who to get a enum inside the setCategory onChange event??????
        //who to casting string to enum
        
      </form>
      {
        coupons.map((coupon) => {
          return (
            <>
              <tr key={coupon.id} className='coupon'></tr><br />
              <td>{coupon.id}</td><br />
              <td>{coupon.category}</td><br />
              <td>{coupon.price}</td><br />
              <td>{coupon.amount}</td><br />
              <td>{coupon.endDate}</td><br />
              <td>{coupon.image}</td><br />
            </>)
        }

        )}
    </div>
    )
}
export default CompanyGetAllCoupons
