import { FormEvent, useEffect, useState } from 'react'
import { Category, Coupon } from '../../Entitis.type';
interface Props {
  token: string | undefined
  id: number | undefined
}
function CompanyGetCouponsByCategory(props: Props) {

  const [coupons, setCoupons] = useState<Array<Coupon>>([]);
  const [category, setCategory] = useState<Category>()
  const [loading, setLoading] = useState<boolean>(false);
  const [hasError, setHasError] = useState<boolean>(false);
  console.log(props.token);

  const GetByMaxPrice = async (event: FormEvent<HTMLFormElement>): Promise<void> => {

    event.preventDefault();

    try {
      const response = await fetch(
        'http://localhost:8080/company/getCoupons/category?category=' + category + '&companyId=' + props.id,
        {
          method: 'GET',
          headers: {
            'Authorization': props.token!,
            'Content-Type': 'application/json',
          },
          mode: 'cors'
        });
      if (response.ok) {
        const data = await response.json();
        setCoupons(data);
        console.log(data); // log response data to console
      } else {
        console.log('Response not ok');
        setHasError(true);
      }
    } catch (error) {
      console.log(error);
      setHasError(true);
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className='getAllCompanys'>
      <form>
        <select value={category} onChange={(event) => setCategory(event.target.value as Category)}>
          {Object.values(Category).map((category) => (
            <option key={category} value={category}>
                     {coupons.map((coupon) => (
              <tr key={coupon.id}>
                <td>{coupon.id}</td>
                <td>{coupon.category}</td>
                <td>{coupon.price}</td>
                <td>{coupon.amount}</td>
                <td>{coupon.endDate}</td>
              </tr>
            ))}
            </option>
          ))}
        </select>
        <button type="submit">Get Coupons By Category: </button>
      </form>
      {loading && <p>Loading...</p>}
      {hasError && <p>An error occurred while fetching data.</p>}
      {!loading && !hasError && (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Category</th>
              <th>Price</th>
              <th>Amount</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {coupons.map((coupon) => (
              <tr key={coupon.id}>
                <td>{coupon.id}</td>
                <td>{coupon.category}</td>
                <td>{coupon.price}</td>
                <td>{coupon.amount}</td>
                <td>{coupon.endDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};


export default CompanyGetCouponsByCategory
