import React, { FormEvent, useEffect, useState } from 'react'
import { Category, Coupon } from '../../Entitis.type';
interface Props {
    token: string;
    id:number
   category:Category
  }
function CompanyGetCouponsByCategory(props: Props) {
  
  const [coupons, setCoupons] = useState<Coupon>(
    {
      id: undefined, category: undefined,
      startDate: '',
      endDate: '',
      amount: undefined,
      price: undefined,
      image: ''
    });

  const [category, setMaxPrice] = useState<number | undefined>()

  console.log(props.token);
  
  const getByMaxPrice = (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
    useEffect(() => {
      fetch('http://localhost:8080/company/getCoupons/category?category=' + props.id + '&maxPrice=' + props.category, {
        method: 'GET',
        headers: {
          'Authorization': props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      }).then((res) => res.json()).then((data) => {
        setCoupons(data)
        console.log(data); // log response data to console
      }).catch((error) => console.log(error));
    }, []); // add empty array to run useEffect only once on component mount
  }

  return (
    <div className='getAllCompanys'>
      <form onSubmit={getByMaxPrice}>
        <label>Max Price:</label>
        <input type="maxprice" value={maxPrice} onChange={(event) => setMaxPrice(parseInt(event.target.value))} />
        <button type="submit">my max price:</button>
      </form>
      {maxPrice === undefined ? null :
        <>
          <td>{coupons.id}</td><br />
          <td>{coupons.category}</td><br />
          <td>{coupons.price}</td><br />
          <td>{coupons.amount}</td><br />
          <td>{coupons.endDate}</td><br />
          <td>{coupons.image}</td><br />
        </>
      }
    </div>
  )
}
 

export default CompanyGetCouponsByCategory
