import {  Customer } from '../../Entitis.type';
import { useState, FormEvent, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

type Props = {
  token: string
};

const GetOneCustomer = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const companyId = parseInt(id)
  const [customer, setCustomer] = useState<Customer>({ id: companyId, firstName: '',lastName:'', email: '', password: 0, coupons: [] });
 

  useEffect(() => {
    fetch('http://localhost:8080/admin/customer?customerId=' + id, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCustomer(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, [])

  return (
    <div className='getOneCompanys'>
          <button onClick={() => navigate(-1)}>Back</button> {/* add "Back" button */}
          <div className='companyDetails'>
        <div>ID: {companyId !== 0 ? customer?.id : null}</div>
        <div>Name: {companyId !== 0 ? customer?.name : null}</div>
        <div>Email: {companyId !== 0 ? company?.email : null}</div>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Category</th>
              <th>Price</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {companyId !== 0 ? company?.coupons.map((coupon) => {
              return (
                <tr key={coupon.id}>
                  <td>{coupon.id}</td>
                  <td>{coupon.category}</td>
                  <td>{coupon.price}</td>
                  <td>{coupon.endDate}</td>
                </tr>
              )
            }) : null}
          </tbody>
        </table>
      </div>
      <div className='updateDeleteLinks'>
        {companyId !== 0 ?
          <Link to={`/updateCompany/${company.id}`} className="link link-update">
          Update me
        </Link>
         : null}  
        {companyId !== 0 ?
          <Link to={`/deleteCompany/${company.id}`} className="link link-delete">
          Delete company!
        </Link> : null}
      </div>
    </div>
  )
}

export default GetOneCustomer;
