import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import '../../Design/GetAllCompanys.css'

type Props = {
  token: string
};
const GetAllCompanys = (props: Props) => {

  // //context.type
  const [company, setCompany] = useState<Array<Company>>([]);
  console.log(props.token);
  useEffect(() => {
    fetch('http://localhost:8080/admin/company/all', {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, [c]); // add empty array to run useEffect only once on component mount

  
    return (
      <div className='getAllCompanys'>
        {company.map((companys) => {
          return (
            <div key={companys.id} className='company'>
              <div className='company-name'>{companys.name}</div>
              <div className='coupons'>
                {companys.coupons.map((coupons) => {
                  return (
                    <div key={coupons.id} className='coupon'>
                      {coupons.category && <div className='category'>{coupons.category}</div>}
                      {coupons.price && <div className='price'>{coupons.price}</div>}
                    </div>
                  )
                })}
              </div>
            </div>
          )
        })}
      </div>

  )
}

export default GetAllCompanys;
