import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

interface Props {
  token: string | undefined;
  id: number | undefined;
}

const CustomerPurchaseCoupon = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const couponId = parseInt(id);
  const [status, setStatus] = useState<'success' | 'error' | null>(null);
  const [getInFetch, setGetInFetch] = useState(false);

  const purchaseCoupon = () => {
    fetch(
      `http://localhost:8080/customer/purchaseCoupon?couponId=${couponId}&customerId=${props.id}`,
      {
        method: 'POST',
        headers: {
          Authorization: props.token!,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
      }
    )
      .then(() => {
        setStatus('success');
        setGetInFetch(true);
        console.log('purchase problem');
        setTimeout(() => {
          navigate('/allCoupons');
        }, 3000); // <-- Navigate back after 3 seconds
      })
      .catch(() => {
        setStatus('error');
        setTimeout(() => {
          navigate('/allCoupons');
        }, 3000); // <-- Navigate back after 3 seconds
      });
  };

  useEffect(() => {
    return () => {
      setGetInFetch(false);
    };
  }, []); // <-- Empty array means this effect runs only once

  return (
    <div className='delete-company'>
      {status === 'success' && (
        <div>
          Coupon purchased successfully! You will be redirected back in 3 seconds.
        </div>
      )}
      {status === 'error' && (
        <div>
          Error purchasing coupon! You will be redirected back in 3 seconds.
        </div>
      )}
      {!getInFetch && (
        <button onClick={purchaseCoupon}>Purchase Coupon</button>
      )}
    </div>
  );
};

export default CustomerPurchaseCoupon;
