import { useState, useCallback, FormEvent, useContext, useEffect } from 'react';
import '../Design/App.css'
import { HomeCustomer } from './Customer/HomeCustomer';
import { HomeCompany } from './Company/HomeCompany';
import HomeAdmin from './Admin/HomeAdmin';
import { CustomerContext } from '../contexts/AppContextCustomer';
import { fetchCompanyIdByEmail, fetchCustomerIdByEmail } from '../services/ServerService';
import { UserType } from '../Entitis.type';


function Login(): JSX.Element {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState<UserType>(UserType.ADMINISTRATOR);
  const [ loginToken, setLoginToken ]= useState('')
  const [logId, setLogId] = useState<number | undefined>();
  const [badRequest, setBadRequest] = useState(false);

  const login = useCallback(async (event: FormEvent<HTMLFormElement>): Promise<void> => {
    event.preventDefault(); // prevent page refresh
    await fetch('http://localhost:8080/login', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: email,
        password: password,
        userType: userType,
      }),
      mode: 'cors',
      method: 'POST',
    })
      .then((data) => data.text())
      .then((token) => setLoginToken(token))
      .catch((error) => {
        console.log(error);
        setBadRequest(true);
      });
  }, [loginToken]);

  useEffect(() => {
    if (badRequest) {
      setEmail('');
      setPassword('');
      setUserType(UserType.ADMINISTRATOR);
      setBadRequest(false);
    }
  }, [badRequest]);

  const userHandler = () => {
    if (loginToken !== '' && userType === 'ADMINISTRATOR') {
      return (
        <HomeAdmin token={loginToken} />
      )
    }

    if (loginToken !== '' && userType === 'CUSTOMER') {
      // Use the fetchCustomerIdByEmail function to get the customer ID
      fetchCustomerIdByEmail(email).then(id => setLogId(id));
      return (<HomeCustomer token={loginToken} id={logId} />)
    }

    if (loginToken !== '' && userType === 'COMPANY') {
      fetchCompanyIdByEmail(email).then(id => setLogId(id));
      return <HomeCompany token={loginToken} id={logId} />;
    }

    // handle case where token is not received
    return (
      <div>
        <p>Login unsuccessful. Please check your email and password and try again.</p>
        <button onClick={() => setLoginToken('')}>Back to login</button>
      </div>
    );
  }

  return (
    <div className="login">
      {loginToken === '' ? <form onSubmit={login}>
        <label>Email: </label>
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} /><br />
        <label>Password: </label>
        <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} /><br />
        <label>UserType:</label>
        <select value={userType} onChange={(event) => setUserType(event.target.value as UserType)}>
          {Object.values(UserType).map((userType) => (
            <option key={userType} value={userType}>
              {userType}
            </option>
          ))}
        </select>
        <button type="submit">Login</button>
      </form>
        :
        userHandler()
      }
    </div>
  );
}

export default Login;
