import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import { Category, Coupon } from '../../Entitis.type';

interface Props {
    token: string | undefined
    id: number | undefined
}

const GetAllCoupons = (props: Props) => {
    const navigate = useNavigate();
    
    const [coupons, setCoupons] = useState<Array<Coupon>>(
        {
          id: props.id , 
          category: Category.FOOD,
          startDate: '',
          endDate: '',
          amount: 0,
          price: 0,
          image: ''
        });
    console.log(props.token);

    useEffect(() => {
        fetch('http://localhost:8080/customer/allCoupons?customerId=' + props.id,
            {
                method: 'GET',
                headers: {
                    Authorization: props.token!,
                    'Content-Type': 'application/json',
                },
                mode: 'cors',
            }
        ).then((res) => res.json()).then((data) => {
            setCoupons(data);
            console.log(data); // log response data to console
        }).catch((error) => console.log(error))
    }, [])

    return (
        <div className="companyGetAllCoupons">
            <button onClick={() => navigate('/')}>Back</button>
            <table>
                <thead>
                    <tr>
                        <th>Coupon Purchase:</th>
                        <th>Category:</th>
                        <th>Start Date:</th>
                        <th>End Date:</th>
                        <th>Amount:</th>
                        <th>Price:</th>
                        <th>Image:</th> {/* add a column for the image */}
                    </tr>
                </thead>
                <tbody>
                    {coupons.map((coupon) => (
                        <tr key={coupon.id}>
                            <td>{coupon.category}</td>
                            <td>{coupon.startDate}</td>
                            <td>{coupon.endDate}</td>
                            <td>{coupon.amount}</td>
                            <td>{coupon.price}</td>
                            <td>{coupon.image}</td>
                            <td><img src={coupon.image} alt="#" /></td> {/* display the image */}
                            <td>
                                <Link to={`/purchaseCoupon/${coupon.id}`}>
                                    <td>purchase</td>
                                </Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default GetAllCoupons;
