import React, { FormEvent, useEffect, useState } from 'react';
import { Category, Coupon } from '../../Entitis.type';

interface Props {
  token: string;
  id: number;
  category: Category;
}

function CompanyGetCouponsByCategory(props: Props) {
  const [coupons, setCoupons] = useState<Array<Coupon>>([]);
  const [category, setCategory] = useState<Category | undefined>();

  console.log(props.token);

  const fetchData = (): void => {
    fetch(
      `http://localhost:8080/company/getCoupons/category?category=${props.id}&maxPrice=${category?.name}`,
      {
        method: 'GET',
        headers: {
          Authorization: props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
      }
    )
      .then((res) => res.json())
      .then((data) => {
        setCoupons(data);
        console.log(data); // log response data to console
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    fetchData();
  }, [category]);

  const handleCategoryChange = (event: FormEvent<HTMLInputElement>): void => {
    const categoryName = event.currentTarget.value;
    const categoryObj: Category = { id: props.category.id, name: categoryName };
    setCategory(categoryObj);
  };

  return (
    <div className='getAllCompanys'>
      <form onSubmit={(event) => event.preventDefault()}>
        <label>Max Price:</label>
        <input type='maxprice' value={category?.name ?? ''} onChange={handleCategoryChange} />
        <button type='submit' onClick={fetchData}>
          my max price:
        </button>
      </form>
      {coupons.map((coupon) => {
        return (
          <div key={coupon.id} className='coupon'>
            <div>{coupon.id}</div>
            <div>{coupon.category}</div>
            <div>{coupon.price}</div>
            <div>{coupon.amount}</div>
            <div>{coupon.endDate}</div>
            <div>{coupon.image}</div>
          </div>
        );
      })}
    </div>
  );
}

export default CompanyGetCouponsByCategory;
