import React, { FormEvent, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

interface Props {
  token: string | undefined;
  companyId: number | undefined;
}

const CompanyDeleteCoupon = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const couponId = parseInt(id);

  const [notification, setNotification] = useState('');

 
    console.log(props.token);

    fetch(
      `http://localhost:8080/company/deleteCoupon?companyId=${props.companyId}&couponId=${couponId}`,
      {
        method: 'POST',
        headers: {
          Authorization: props.token!,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
      }
    )
      .then(() => {
        // set notification message and navigate back only when the fetch request succeeds
        setNotification('Coupon deleted successfully');
        setTimeout(() => navigate(-1), 3000); // navigate back after 3 seconds
      })
      .catch((error) => console.log(error));
  

  return (
    <div className="delete-company">
      <button onClick={() => navigate(-1)}>Back</button>
      {notification && <p>{notification}</p>}
    </div>
  );
};

export default CompanyDeleteCoupon;
