import {  Customer } from '../../Entitis.type';
import { useState, FormEvent } from 'react';

type Props = {
  token: string
};

const GetOneCustomer = (props: Props) => {

  const [customer, setCustomer] = useState<Customer>();
  const [id, setId] = useState<number>(0);

  const getOneCustomer = (event: FormEvent<HTMLFormElement>): void => {

    event.preventDefault();
  
    fetch('http://localhost:8080/admin/customer?customerId=' + id, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCustomer(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }

  return (
    <div className='getOneCompanys'>
      <form onSubmit={getOneCustomer}>
        <label>id:</label>
        <input type="id" value={id} onChange={(event) => setId(parseInt(event.target.value))} />
        <button type="submit">get one customer</button>
      </form>
      <div>{id !== 0 ? customer?.id : null}</div>
      <div>{id !== 0 ? customer?.firstName : null}</div>
      <div>{id !== 0 ? customer?.lastName : null}</div>
      <div>{id !== 0 ? customer?.email : null}</div>
      <div>{id !== 0 ? customer?.coupons.map((coupon) => {
        return (<>
          <td>{coupon.id}</td><br />
          <td>{coupon.category}</td><br />
          <td>{coupon.price}</td><br />
          <td>{coupon.endDate}</td><br />
        </>)
      }) : null}</div>
    </div>
  )
}

export default GetOneCustomer;
