import { FormEvent, JSXElementConstructor, Key, ReactElement, ReactFragment, ReactPortal, useEffect, useState } from 'react'
import { Coupon } from '../../Entitis.type';
interface Props {
  token: string|undefined
  id: number |undefined
}
const CompanyGetCouponsByMaxPrice = (props: Props) => {
  const [coupons, setCoupons] = useState<Array<Coupon>>([]);

  const [maxPrice, setMaxPrice] = useState<number>(0)

  console.log(props.token);

  const GetByMaxPrice = (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
   
      fetch('http://localhost:8080/company/getCoupons/maxToPay?companyId=' + props.id + '&maxPrice=' + maxPrice, {
        method: 'GET',
        headers: {
          'Authorization': props.token!,
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      }).then((res) => res.json()).then((data) => {
        setCoupons(data)
        console.log(data); // log response data to console
      }).catch((error) => console.log(error));
     // add empty array to run useEffect only once on component mount
  }

   return (
    <div className='getAllCompanys'>
      <form onSubmit={GetByMaxPrice}>
        <label>Max Price:</label>
        <input type="maxprice" value={maxPrice} onChange={(event) => setMaxPrice(parseInt(event.target.value))} />
        <button type="submit">my max price:</button>

      </form>
      { !GetByMaxPrice? null : (
        <table>
          <tbody>
            {coupons.map((coupon) => {
        return (<>
              <tr key={coupon.id}>
                <td>{coupon.id}</td>
                <td>{coupon.category}</td>
                <td>{coupon.price}</td>
                <td>{coupon.amount}</td>
                <td>{coupon.endDate}</td>
                {/* <td>{coupon.image}</td> */}
              </tr>
              </>)
      }
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default CompanyGetCouponsByMaxPrice;
