import { FormEvent, useEffect, useState } from 'react';
import { Category, Coupon } from '../../Entitis.type';



interface Props {
  token: string | undefined;
  id: number | undefined;
}

function CompanyGetCouponsByCategory(props: Props) {
  const [coupons, setCoupons] = useState<Array<Coupon>>([]);
  const [category, setCategory] = useState<Category>();
  const [loading, setLoading] = useState<boolean>(false);
  const [hasError, setHasError] = useState<boolean>(false);

  const getCouponsByCategory = async (event: FormEvent<HTMLFormElement>): Promise<void> => {
    event.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(
        `http://localhost:8080/company/getCoupons/category?category=${category}&companyId=${props.id}`,
        {
          method: 'GET',
          headers: {
            Authorization: props.token!,
            'Content-Type': 'application/json',
          },
          mode: 'cors',
        }
      );
      if (response.ok) {
        const data = await response.json();
        setCoupons(data);
      } else {
        setHasError(true);
      }
    } catch (error) {
      setHasError(true);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="getAllCompanys">
      <form onSubmit={getCouponsByCategory}>
        <label>Category:</label>
        <select value={category} onChange={(event) => setCategory(event.target.value as CategoryType)}>
          <option value={Category.FOOD}>Food</option>
          <option value={Category.ELECTRONICS}>Electronics</option>
          <option value={CategoryType.CLOTHING}>Clothing</option>
          <option value={CategoryType.TRAVEL}>Travel</option>
        </select>
        <button type="submit">Get Coupons By Category</button>
      </form>
      {loading && <p>Loading...</p>}
      {hasError && <p>An error occurred while fetching data.</p>}
      {!loading && !hasError && (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Category</th>
              <th>Price</th>
              <th>Amount</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {coupons.map((coupon) => (
              <tr key={coupon.id}>
                <td>{coupon.id}</td>
                <td>{coupon.category}</td>
                <td>{coupon.price}</td>
                <td>{coupon.amount}</td>
                <td>{coupon.endDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default CompanyGetCouponsByCategory;
