import React, { useState, useCallback, FormEvent } from 'react';
import HomeAdmin from './HomeAdmin'
import './App.css';
import { HomeCustomer } from './HomeCustomer';
import { HomeCompany } from './HomeCompany';

function App(): JSX.Element {
  const [loginToken, setLoginToken] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');

  const login = useCallback((event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault(); // prevent page refresh

    fetch('http://localhost:8080/login', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: email,
        password: password,
        userType: userType,
      }),
      mode: 'cors',
      method: 'POST',
    })
      .then((data) => data.text())
      .then((token) => setLoginToken(token))
      .catch((error) => console.log(error));
  }, [email, password, userType]);

  const userHendler=()=>{
    if(loginToken != '' && userType=='ADMINISTRATOR'){
      {
      return<HomeAdmin token={loginToken}/>}
    }
    if(loginToken != '' && userType=='CUSTOMER'){
      <HomeCustomer/>
    }
    if(loginToken!=''&&userType=='COMPANY'){
      <HomeCompany/>
    }
  }
  console.log(loginToken);

  return (
    <div className="App">
      <form onSubmit={login}>
        <label>Email: </label>
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} /><br/>
        <label>Password: </label>
        <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} /><br/>
        <label>User Type: </label>
        <input type="text" value={userType} onChange={(event) => setUserType(event.target.value)} /><br/>
        <button type="submit">Login</button>
        <div>

        </div>
      </form>

    </div>
  );
}

export default App;
