import React, { FormEvent, useState } from 'react'
import { useNavigate } from 'react-router-dom';
interface Props {
  token: string|undefined
  companyId:number|undefined

}
const CompanyDeleteCoupon = (props: Props) => {
  const navigate = useNavigate();
    const [couponId, setCouponId]=useState<number>()

const deleteCoupon =(event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault(); // prevent page refresh
    console.log(props.token);
   
    fetch('http://localhost:8080/company/deleteCoupon?companyId='+props.companyId+'&couponId='+couponId, { 
      method: 'POST',
      headers: {
        'Authorization': props.token!,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).catch((error) => console.log(error));
  }
  const addCoupon = (event: React.MouseEvent): void => {
    event.preventDefault();
    console.log(props.token);
  
    // check if required fields are filled in
    if (!coupon.category || !coupon.price || !coupon.amount || !coupon.endDate) {
      alert('Please fill in all required fields.');
      return;
    }
  
    // check if the price and amount are valid numbers
    if (isNaN(coupon.price) || isNaN(coupon.amount)) {
      alert('Price and amount must be valid numbers.');
      return;
    }
  
    fetch('http://localhost:8080/company/addCoupon?companyId=' + props.id, {
      method: 'POST',
      headers: {
        Authorization: props.token!,
        'Content-Type': 'application/json',
      },
      mode: 'cors',
      body: JSON.stringify(coupon),
    }).then(() => {
      navigate(-1);
    }).catch((error) => console.log(error));
  } 
  
  return (
    <div className='delete-company'>
      <button onClick={() => navigate(-1)}>Back</button> {/* add "Back" button */}
      {<form onSubmit={deleteCoupon}>
        <label>id: </label>
        <input type="id" value={couponId} onChange={(event) => setCouponId(parseInt(event.target.value))}  />
        <button type="submit">delete Coupon</button>
      </form>}
    </div>
);
}
export default CompanyDeleteCoupon
