import { useState, useCallback, FormEvent } from 'react';
import '../Design/App.css'
import { HomeCustomer } from './Customer/HomeCustomer';
import { HomeCompany } from './Company/HomeCompany';
import HomeAdmin from './Admin/HomeAdmin';
import { CustomerProvider, UseCustomerContext} from '../contexts/AppContextCustomer';
import { Company, Customer } from '../Entitis.type';

function Login(): JSX.Element {
  const [loginToken, setLoginToken] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const[id1,setId]=useState<number|undefined>();
  const[array,setArray]=useState<Array<Customer|Company>>([]);


  const login = useCallback((event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault(); // prevent page refresh

    fetch('http://localhost:8080/login', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: email,
        password: password,
        userType: userType,
      }),
      mode: 'cors',
      method: 'POST',
    })
      .then((data) => data.text())
      .then((token) => setLoginToken(token))
      .catch((error) => console.log(error));
  }, [email, password, userType]);

  const userHendler = () => {
    if (loginToken !== '' && userType === 'ADMINISTRATOR') {
      return (
          <HomeAdmin token={loginToken}/>
      )
    }

    if (loginToken !== '' && userType === 'CUSTOMER') {
      const customers = UseCustomerContext();
      const filteredCustomers = customers.filter((customer) => customer.email === email);
      const customerArray = filteredCustomers.length ? [filteredCustomers[0]] : [];
      setId(customerArray[0]?.id);
      return (

        <CustomerProvider token={loginToken}>
          <HomeCustomer token={loginToken} id={id1} />
        </CustomerProvider>
      );
    }
      if (loginToken !== '' && userType === 'COMPANY') {
        return <HomeCompany token={loginToken} />
      }

      // Add a default return statement
      return null;
    }
    console.log(loginToken);

  return (

      <div className="login">

        {loginToken === '' ? <form onSubmit={login}>{/*move login to different component*/}
          <label>Email: </label>
          <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} /><br />
          <label>Password: </label>
          <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} /><br />
          <label>User Type: </label>
          <input type="userType" value={userType} onChange={(event) => setUserType(event.target.value)} /><br />
          <button type="submit">Login</button>
        </form>
          :

          userHendler()

        }

      </div>


  );
}

export default Login;
