import { useState, useCallback, FormEvent, useContext } from 'react';
import '../Design/App.css'
import { HomeCustomer } from './Customer/HomeCustomer';
import { HomeCompany } from './Company/HomeCompany';
import HomeAdmin from './Admin/HomeAdmin';
import { CustomerContext } from '../contexts/AppContextCustomer';
import { fetchCustomerIdByEmail } from '../services/ServerService';


function Login(): JSX.Element {
//  const [loginToken, setLoginToken] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const {loginToken,setLoginToken,customers} = useContext(CustomerContext);

  const login = useCallback(async (event: FormEvent<HTMLFormElement>): Promise<void> => {
    event.preventDefault(); // prevent page refresh
   await fetch('http://localhost:8080/login', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: email,
        password: password,
        userType: userType,
      }),
      mode: 'cors',
      method: 'POST',
    })
      .then((data) => data.text())
      .then((token) => setLoginToken(token))
      .catch((error) => console.log(error));
  }, [email, password, userType]);

  const userHendler = () => {
    if (loginToken !== '' && userType === 'ADMINISTRATOR') {
      return (
          <HomeAdmin token={loginToken}/>
      )
    }

    if (loginToken !== '' && customers.length && userType === 'CUSTOMER') {
     const id= fetchCustomerIdByEmail(email)
     const intId=parseInt(id)
      return(<HomeCustomer token={loginToken} id={i}/>)
    }
      if (loginToken !== '' && userType === 'COMPANY') {
      //  return <HomeCompany token={loginToken} id={id3}/>
      }

      // Add a default return statement
      return null;
    }
   // add this closing curly brace

  console.log(loginToken);

  return (

    <div className="login">
      {loginToken === '' ? <form onSubmit={login}>{/*move login to different component*/}
        <label>Email: </label>
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} /><br />
        <label>Password: </label>
        <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} /><br />
        <label>User Type: </label>
        <input type="userType" value={userType} onChange={(event) => setUserType(event.target.value)} /><br />
        <button  type="submit">Login</button>
      </form>
        :
        userHendler()
      }

    </div>


  );
}

export default Login;
