import React, { useState } from 'react'
import { Coupon, Category } from '../../Entitis.type';
interface Props {
    token: string|undefined;
    id:number|undefined
  }
const CustomerPurchaseCoupon = (props: Props) => {
 const [coupon, setCoupon] = useState<Coupon>(
    {
      id: 0, category: Category.FOOD,
      startDate: '',
      endDate: '',
      amount: 0,
      price: 0,
      image: ''
    });
  const addCoupon = (event: React.MouseEvent): void => {
    event.preventDefault();
    console.log(props.token);

    fetch('http://localhost:8080/customer/purchaseCoupon?couponId='+coupon.id+'&customerId=' + props.id, {
      method: 'POST',
      headers: {
        Authorization: props.token!,
        'Content-Type': 'application/json',
      },
      mode: 'cors',
      body: JSON.stringify(coupon),
    }).catch((error) => console.log(error));
  }

  
  return (
    <form>
      <label>Id: </label>
      <input type="id" value={coupon.id} onChange={(event) => setCoupon((prev: any) => ({ ...prev, id: event.target.value }))} /><br />
      <label>Category:</label>
      <select value={coupon.category} onChange={(event) => setCoupon((prev: Coupon) => ({ ...prev, category: event.target.value as Category }))}><br />
        {Object.values(Category).map((category) => (
          <option key={category} value={category}>
            {category}
          </option>
        ))}
        </select><br />
      <label>Price: </label>
      <input type="price" value={coupon.price} onChange={(event) => setCoupon((prev: any) => ({ ...prev, price: event.target.value }))} /><br />
      <label>Amount: </label>
      <input type="amount" value={coupon.amount} onChange={(event) => setCoupon((prev: any) => ({ ...prev, amount: event.target.value }))} /><br />
      <label>End Date Coupon: </label>
      <input type="amount" value={coupon.endDate} onChange={(event) => setCoupon((prev: any) => ({ ...prev, endDate: event.target.value }))} /><br />
      <button type="button" onClick={addCoupon}>purchase</button>
    </form>
  )
}

export default CustomerPurchaseCoupon
