import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';

type Props = {
  token: string
};

const GetOneCompany = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const companyId = parseInt(id)
  const [company, setCompany] = useState<Company>({ id: companyId, name: '', email: '', password: 0, coupons: [] });

  useEffect(() => {
    fetch('http://localhost:8080/admin/getCompany?id=' + companyId, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, [])

  return (
    <div className='getOneCompany'>
      <button onClick={() => navigate(-1)}>Back</button> {/* add "Back" button */}
      <div>{companyId !== 0 ? company?.id : null}</div>
      <div>{companyId !== 0 ? company?.name : null}</div>
      <div>{companyId !== 0 ? company?.email : null}</div>
      <div>{companyId !== 0 ? company?.coupons.map((coupon) => {
        return (
          <div key={coupon.id}>
            <div>{coupon.id}</div>
            <div>{coupon.category}</div>
            <div>{coupon.price}</div>
            <div>{coupon.endDate}</div>
          </div>
        )
      }) : null}</div>

      <div>
        {companyId !== 0 ?
          <Link to={`/updateCompany/${company.id}`}>
            Update me
          </Link> : null}
        {companyId !== 0 ?
          <Link to={`/deleteCompany/${company.id}`}>
            Delete company!
          </Link> : null}
        {companyId !== 0 ?
          <Link to={`/updateCompany/${company.id}`}>
            <button>Edit</button>
          </Link> : null}
      </div>
    </div>
  )
}

export default GetOneCompany;

export default GetOneCompany;
