import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import { Coupon } from '../../../Entitis.type';

interface Props {
  token: string | undefined
  id: number | undefined
}

const GetAllCoupons = (props: Props) => {
  const navigate = useNavigate();

  const [coupons, setCoupons] = useState<Array<Coupon>>([]);

  console.log(props.token);

  useEffect(() => {
    console.log('all coupons problem')
    fetch('http://localhost:8080/customer/allCoupons?customerId=' + props.id,
      {
        method: 'GET',
        headers: {
          Authorization: props.token!,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
      }
    ).then((res) => res.json()).then((data) => {
      setCoupons(data);
      console.log(data); // log response data to console
    }).catch((error) => console.log(error))
  }, [])

  const [clickedCouponIds, setClickedCouponIds] = useState<number[]>([]);

  const handlePurchaseClick = (coupon: Coupon) => {
    if (coupon.amount <= 0) {
      alert('Coupon out of stock');
      return;
    }
    setClickedCouponIds([...clickedCouponIds, coupon.id!]);
  }

  return (
    <div className="companyGetAllCoupons">
      <button onClick={() => navigate('/')}>Back</button>
      <table>
        <thead>
          <tr>
            <th>index:</th>
            <th>Coupon Purchase:</th>
            <th>Category:</th>
            <th>Start Date:</th>
            <th>End Date:</th>
            <th>Amount:</th>
            <th>Price:</th>
            <th>Image:</th>
          </tr>
        </thead>
        <tbody>
          {coupons.map((coupon, index) => (
            <tr key={coupon.id || index}>
              <td>{index+1}</td>
              <td>
                {clickedCouponIds.includes(coupon.id!) ? (
                  <span>Coupon purchased!</span>
                ) : (
                  <button 
                    className='professional-button' 
                    onClick={() => handlePurchaseClick(coupon)}
                    disabled={coupon.amount <= 0}
                  >
                    {coupon.amount <= 0 ? 'Out of stock' : 'Purchase'}
                  </button>
                )}
              </td>
              <td>{coupon.category}</td>
              <td>{coupon.startDate}</td>
              <td>{coupon.endDate}</td>
              <td>{coupon.amount}</td>
              <td>{coupon.price}</td>
              <td><img src={coupon.image} alt="#" /></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default GetAllCoupons;
