import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import '../..';

type Props = {
  token: string
};

const GetOneCompany = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const companyId = parseInt(id)
  const [company, setCompany] = useState<Company>({ id: companyId, name: '', email: '', password: 0, coupons: [] });

  useEffect(() => {
    fetch('http://localhost:8080/admin/getCompany?id=' + companyId, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, [])

  return (
    <div className='getAllCompanys'>
      <button onClick={() => navigate(-1)}>Back</button>
      <div className='companyDetails'>
        <div>ID: {companyId !== 0 ? company?.id : null}</div>
        <div>Name: {companyId !== 0 ? company?.name : null}</div>
        <div>Email: {companyId !== 0 ? company?.email : null}</div>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Category</th>
              <th>Price</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {companyId !== 0 ? company?.coupons.map((coupon) => {
              return (
                <tr key={coupon.id}>
                  <td>{coupon.id}</td>
                  <td>{coupon.category}</td>
                  <td>{coupon.price}</td>
                  <td>{coupon.endDate}</td>
                </tr>
              )
            }) : null}
          </tbody>
        </table>
      </div>
      <div className='updateDeleteLinks'>
        {companyId !== 0 ?
          <Link to={`/updateCompany/${company.id}`}>
            Update Company
          </Link> : null}  
        {companyId !== 0 ?
          <Link to={`/deleteCompany/${company.id}`}>
            Delete Company
          </Link> : null}
      </div>
    </div>
  )
}

export default GetOneCompany;
