import { Company } from '../../Entitis.type';
import { useState, FormEvent } from 'react';

type Props = {
  token: string
};

const GetOneCompany = (props: Props) => {

  const [company, setCompany] = useState<Company>();
  const [id, setId] = useState<number>(0);

  const getOneCompany = (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
  
    fetch('http://localhost:8080/admin/getCompany?id=' + id, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }

  return (
    <div className='getAllCompanys'>
      <form onSubmit={getOneCompany}>
        <label>id:</label>
        <input type="id" value={id} onChange={(event) => setId(parseInt(event.target.value))} />
        <button type="submit">get One Company</button>
      </form>
      <div>{id !== 0 ? company?.id : null}</div>
      <div>{id !== 0 ? company?.name : null}</div>
      <div>{id !== 0 ? company?.email : null}</div>
      <div>{id !== 0 ? company?.coupons.map((coupon) => {
        return (<>
          <td>{coupon.id}</td><br />
          <td>{coupon.category}</td><br />
          <td>{coupon.price}</td><br />
          <td>{coupon.endDate}</td><br />
        </>)
      }) : null}</div>
    </div>
  )
}

export default GetOneCompany;
