import React, { useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import { Coupon, Category } from '../../Entitis.type';
interface Props {
    token: string|undefined;
    id:number|undefined
  }
const CustomerPurchaseCoupon = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const couponId = parseInt(id);
 const [coupon, setCoupon] = useState<Coupon>(
    {
      id: 0, category: Category.FOOD,
      startDate: '',
      endDate: '',
      amount: 0,
      price: 0,
      image: ''
    });
  const addCoupon = (event: React.MouseEvent): void => {
    event.preventDefault();
    console.log(props.token);

    fetch('http://localhost:8080/customer/purchaseCoupon?couponId='+coupon.id+'&customerId=' + props.id, {
      method: 'POST',
      headers: {
        Authorization: props.token!,
        'Content-Type': 'application/json',
      },
      mode: 'cors',
      body: JSON.stringify(coupon),
    }).catch((error) => console.log(error));
  }

  
  return (
    <div className='delete-company'>
    {status === 'success' && (
      <div>
        Coupon deleted successfully! You will be redirected back in 3 seconds.
      </div>
    )}
    {status === 'error' && (
      <div>
        Error deleting Coupon! You will be redirected back in 3 seconds.
      </div>
    )}
  </div>
  )
}

export default CustomerPurchaseCoupon
