import React, { useState, useCallback, FormEvent } from 'react';
import HomeAdmin from './Admin/GetAllCompanys'
import '../Design/App.css'
import { HomeCustomer } from './Customer/HomeCustomer';
import { HomeCompany } from './Company/HomeCompany';
import { UserContext } from '../contexts/AppContext';

function Login(): JSX.Element {
  const [loginToken, setLoginToken] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');

  const login = useCallback((event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault(); // prevent page refresh

    fetch('http://localhost:8080/login', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: email,
        password: password,
        userType: userType,
      }),
      mode: 'cors',
      method: 'POST',
    })
      .then((data) => data.text())
      .then((token) => setLoginToken(token))
      .catch((error) => console.log(error));
  }, [email, password, userType]);

  const userHendler = () => {
    if (loginToken != '' && userType == 'ADMINISTRATOR') {
      return (
        <BrowserRouter>
      <HomeAdmin token={loginToken} />
      </BrowserRouter>
      )
    }

    if (loginToken != '' && userType == 'CUSTOMER') {
      <HomeCustomer />
    }
    if (loginToken != '' && userType == 'COMPANY') {
      <HomeCompany />
    }
  }

  console.log(loginToken);

  return (
    <UserContext.Provider value={{ type: userType}}>
      <div className="App">

        {loginToken === '' ? <form onSubmit={login}>{/*move login to different component*/}
          <label>Email: </label>
          <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} /><br />
          <label>Password: </label>
          <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} /><br />
          <label>User Type: </label>
          <input type="userType" value={userType} onChange={(event) => setUserType(event.target.value)} /><br />
          <button type="submit">Login</button>
        </form>
          :
          userHendler()
        }

      </div>
    </UserContext.Provider>

  );
}

export default Login;
