import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Company } from '../../../Entitis.type';

interface Props {
  token: string;
  password:number
}

function AddCompany(props: Props): JSX.Element {
  const navigate = useNavigate();
  const [company, setCompany] = useState<Company>({ id: 0, name: '', email: '', password: props.password, coupons: [] });

  const addCompany = async (event: React.MouseEvent) => {
    event.preventDefault(); // prevent page refresh
    console.log(props.token);

    try {
      const response = await fetch('http://localhost:8080/admin/company/add', {
        method: 'POST',
        headers: {
          Authorization: props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
        body: JSON.stringify(company),
      });

      if (response.ok) {
        navigate(-1);
      } else {
        console.log('Failed to add company');
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="AddCompany">
      <button onClick={() => navigate(-1)}>Back</button>
      <form>
        <label>Name: </label>
        <input
          type="text"
          value={company.name}
          onChange={(event) => setCompany((prev) => ({ ...prev, name: event.target.value }))}
        />
        <br />
        <label>Email: </label>
        <input
          type="email"
          value={company.email}
          onChange={(event) => setCompany((prev) => ({ ...prev, email: event.target.value }))}
        />
        <br />
        <label>Password: </label>
        <input
          type="password"
          value={company.password}
          onChange={(event) => setCompany((prev) => ({ ...prev, password: event.target.value }))}
        />
        <br />
        <button type="button" onClick={addCompany}>
          Add Company
        </button>
      </form>
    </div>
  );
}

export default AddCompany;
