import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import '../../Design/GetAllCompanys.css'
import { useNavigate } from 'react-router-dom';
import UpdateCompany from './UpdateCompany';


type Props = {
  token: string
  
};
const GetAllCompanys = (props: Props) => {
  const navigate = useNavigate();
  // //context.type
  const [company, setCompany] = useState<Array<Company>>([]);
  console.log(props.token);

  useEffect(() => {
    fetch('http://localhost:8080/admin/company/all', {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, []); // add empty array to run useEffect only once on component mount

  
return (
    <div className="getAllCompanys">
      <button onClick={() => navigate(-1)}>Back</button>
      <table>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Category</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {company.map((company) => {
            return (
              <tr key={company.id}>
                <td>
                  <button onClick={() => handleUpdateCompanyClick(company.id)}>
                    {company.name}
                  </button>
                </td>
                <td>
                  {company.coupons.map((coupon) => {
                    return (
                      <div key={coupon.id}>
                        {coupon.category && <div>{coupon.category}</div>}
                      </div>
                    );
                  })}
                </td>
                <td>
                  {company.coupons.map((coupon) => {
                return (
                  <div key={coupons.id}>
                    {coupons.price && <div>{coupons.price}</div>}
                  </div>
                );
              })}
            </td>
          </tr>
        );
      })}
    </tbody>
  </table>
</div>

  )
}

export default GetAllCompanys;
