import { useEffect, useState } from "react";
import Login from "../Login";
import {CustomerContext } from '../../contexts/AppContextCustomer';
import { fetchCustomers } from "../../services/ServerService";

export default function App(){
    const [custumers, setCustomers] = useState([]);
    const [loginToken,setLoginToken] = useState('');
    useEffect(()=>{
        fetchCustomers(loginToken)
    },[loginToken]);
    return (
            <CustomerContext.Provider value={{custumers,setCustomers,loginToken, setLoginToken}}>
                    <Login />
            </CustomerContext.Provider>
        )
}
