import { createContext, ReactElement, useContext, useEffect, useState } from "react";
import { Customer } from "../Entitis.type";



type Props = {
  token: string
  children: React.ReactNode
};

export const CustomerContext = createContext<Array<Customer>>([]);

export const CustomerProvider: React.FC<Props> = ({ children, token }): ReactElement => {
  const [customers, setCustomers] = useState<Array<Customer>>([]);

   useEffect(() => {
      fetch('http://localhost:8080/admin/customers/all', {
        method: 'GET',
        headers: {
          'Authorization': token,
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      })
        .then((res) => res.json())
        .then((data) => {
          setCustomers(data);
        })
        .catch((error) => {
          console.log(error)
        });
    
  }, [token]);

  return (
    <div className="n">
    <CustomerContext.Provider value={customers}>
      {children} 
    </CustomerContext.Provider>
    </div>
  );
};

