import React, { FormEvent, useContext } from 'react';
import { Company } from '../../Entitis.type';
import { useState, useCallback } from 'react';
import { UserContext } from '../../contexts/AppContext';

interface Props {
  token: string;
}

function UpdateCompany(props: Props): JSX.Element {
  const {type} = useContext(UserContext);
  
  const [company, setCompany] = useState<Company>({ id:0, name: '', email: '',password:0,coupons:[] }); 

  const updateCompany =(event: React.MouseEvent): void => {
      event.preventDefault(); // prevent page refresh
      console.log(props.token);

      fetch('http://localhost:8080/admin/customer/update', { //move to env var
        method: 'POST',
        headers: {
          Authorization: props.token,
          'Content-Type': 'application/json',
        },
        mode: 'cors',
        body: JSON.stringify(company),
      }).catch((error) => console.log(error));//send to logs instead of prnting to console
      }

  return (
    <div className="UpdateCompany">
      <form>{/*const fields = [{label:"id", value:company["id"],onChnage=()=>{}}]
       or use react-hook-form
      */}
        <label>ID: </label>
        <input type="id" value={company.id} onChange={(event) => setCompany((prev: any) => ({ ...prev, id: event.target.value }))} /><br/> {/*<br> is an anti pattern */}
        <label>Name: </label>
        <input type="name" value={company.name} onChange={(event) => setCompany((prev: any) => ({ ...prev, name: event.target.value }))} /><br/>
        <label>Email: </label>
        <input type="email" value={company.email} onChange={(event) => setCompany((prev: any) => ({ ...prev, email: event.target.value }))} /><br/>
        <label>Password: </label>
        <input type="password" value={company.password} onChange={(event) => setCompany((prev: any) => ({ ...prev, password: event.target.value }))} /><br/>
        <button type="button" onClick={updateCompany}>Update</button>
      </form>
      
    </div>
  );
}

export default UpdateCompany;
