import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Coupon } from '../../Entitis.type';

interface Props {
    token: string | undefined;
    id: number | undefined;
}

const GetAllCoupons = (props: Props) => {
    const navigate = useNavigate();
    const [coupons, setCoupons] = useState<Array<Coupon>>([]);

    useEffect(() => {
        console.log('all coupons problem');
        fetch('http://localhost:8080/customer/allCoupons?customerId=' + props.id,
            {
                method: 'GET',
                headers: {
                    Authorization: props.token!,
                    'Content-Type': 'application/json',
                },
                mode: 'cors',
            }
        )
            .then((res) => res.json())
            .then((data) => {
                // Filter out coupons that have already been purchased by the current user
                const filteredCoupons = data.filter((coupon: Coupon) => {
                    return !coupon.customers.includes(props.id);
                });
                setCoupons(filteredCoupons);
            })
            .catch((error) => console.log(error));
    }, []);

    const handlePurchaseCoupon = (couponId: number) => {
        fetch(
            `http://localhost:8080/customer/purchaseCoupon?couponId=${couponId}&customerId=${props.id}`,
            {
                method: 'POST',
                headers: {
                    Authorization: props.token!,
                    'Content-Type': 'application/json',
                },
                mode: 'cors',
            }
        )
            .then(() => {
                // Reload the coupon list after a successful purchase
                fetch('http://localhost:8080/customer/allCoupons?customerId=' + props.id,
                    {
                        method: 'GET',
                        headers: {
                            Authorization: props.token!,
                            'Content-Type': 'application/json',
                        },
                        mode: 'cors',
                    }
                )
                    .then((res) => res.json())
                    .then((data) => {
                        const filteredCoupons = data.filter((coupon: Coupon) => {
                            return !coupon.customers.includes(props.id);
                        });
                        setCoupons(filteredCoupons);
                    })
                    .catch((error) => console.log(error));
            })
            .catch(() => {
                console.log('Error purchasing coupon!');
            });
    };

    return (
        <div className="companyGetAllCoupons">
            <button onClick={() => navigate('/')}>Back</button>
            <table>
                <thead>
                    <tr>
                        <th>Coupon Purchase:</th>
                        <th>Category:</th>
                        <th>Start Date:</th>
                        <th>End Date:</th>
                        <th>Amount:</th>
                        <th>Price:</th>
                        <th>Image:</th> {/* add a column for the image */}
                    </tr>
                </thead>
                <tbody>
                    {coupons.map((coupon, index) => (
                        <tr key={coupon.id || index}>
                            <td>{coupon.category}</td>
                            <td>{coupon.startDate}</td>
                            <td>{coupon.endDate}</td>
                            <td>{coupon.amount}</td>
                            <td>{coupon.price}</td>
                            <td>
                                <img src={coupon.image} alt="#" />
                            </td>
                            <td>
                                <button onClick={() => handlePurchaseCoupon(coupon.id)}>
                                    Purchase
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default GetAllCoupons;
