import { Company } from '../../Entitis.type';
import { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import UpdateCompany from './UpdateCompany';

type Props = {
  token: string
};

const GetOneCompany = (props: Props) => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: any }>();
  const companyId = parseInt(id)
  const [company, setCompany] = useState<Company>({ id: companyId, name: '', email: '', password: 0, coupons: [] });
  const [showUpdateCompany, setShowUpdateCompany] = useState(false);

  useEffect(() => {
    fetch('http://localhost:8080/admin/getCompany?id=' + companyId, {
      method: 'GET',
      headers: {
        'Authorization': props.token,
        'Content-Type': 'application/json',
      },
      mode: 'cors'
    }).then((res) => res.json()).then((data) => {
      setCompany(data)
      console.log(data); // log response data to console
    }).catch((error) => console.log(error));
  }, [])

  return (
    <div className='getAllCompanys'>
      <button onClick={() => navigate(-1)}>Back</button> {/* add "Back" button */}
      <div>{companyId !== 0 ? company?.id : null}</div>
      <div>{companyId !== 0 ? company?.name : null}</div>
      <div>{companyId !== 0 ? company?.email : null}</div>
      <div>{companyId !== 0 ? company?.coupons.map((coupon) => {
        return (<>
        <td key={coupon.id}>{coupon.id}</td><br />
          <td>{coupon.id}</td><br />
          <td>{coupon.category}</td><br />
          <td>{coupon.price}</td><br />
          <td>{coupon.endDate}</td><br />
        </>)
      }) : null}</div>

      <div>
        {companyId !== 0 &&
          <button onClick={() => setShowUpdateCompany(true)}>Update me</button>
        }
        {companyId !== 0 &&
          <Link to={`/deleteCompany/${company.id}`}>
            Delete company!
          </Link>
        }
      </div>

      {showUpdateCompany &&
        <UpdateCompany token={props.token} id={company.id} />
      }
    </div>
  )
}

export default GetOneCompany;
